% file: dispplot.mDispDisplayRatio=1;ConnectOfElem=[1,5,2;               2,6,3;               3,7,4;               4,8,1              ];nEdgePerElem=4;               % calc the total linesTotalLines=0;for ie=1:numele    for iedge=1:nEdgePerElem        inode2=node(ConnectOfElem(iedge,2),ie);                if ( inode2  ~= 0 )         % there exist a mid-node  	           TotalLines=TotalLines+2;        else	                    % there is no mid-node           TotalLines=TotalLines+1;        end       % endif    end % endfor iedgeend   % endfor ieiSparseIndex=zeros(TotalLines,1);jSparseIndex=zeros(TotalLines,1);index=0;for ie=1:numele                   for iedge=1:nEdgePerElem        inode1=node(ConnectOfElem(iedge,1),ie);        inode2=node(ConnectOfElem(iedge,2),ie);        inode3=node(ConnectOfElem(iedge,3),ie);                if ( inode2  ~= 0 )         % there exist a mid-node  	           index=index+1;           iSparseIndex(index)=inode1;           jSparseIndex(index)=inode2;           index=index+1;           iSparseIndex(index)=inode2;           jSparseIndex(index)=inode3;        else	                    % there is no mid-node           index=index+1;           iSparseIndex(index)=inode1;           jSparseIndex(index)=inode3;        end     % endif    end    % endfor idegeend   % endfor ieSpar = sparse(iSparseIndex,jSparseIndex,ones(1,TotalLines));%--------------------------------------------% plot the original mesh and the nodesfigure %(1);gplot(Spar,[coord(1,:)' coord(2,:)'])hold ongplot(eye(numnod),coord','o')title(['Original (undeformed) mesh and nodes. ',int2str(numele),' elements']);hold off;axis equal;%--------------------------------------------% plot original and deformed meshesfigure %(2)gplot(Spar,[coord(1,:)' coord(2,:)'])% coordAfterDeform  is   2  by  2*numnod matrixcoordAfterDeform=[(coord(1,:)+DispDisplayRatio.*disp(1:2:2*numnod-1));                  (coord(2,:)+DispDisplayRatio.*disp(2:2:2*numnod))  ]; hold ongplot(Spar,[coordAfterDeform(1,:)' coordAfterDeform(2,:)'],':')title(['Original and deformed meshes. ',int2str(numele),' elements']);axis equal;